if(typeof(Worker) !== "undefined") {
    w = new Worker("worker.js");
    w.onmessage = function(event) {
        if (event.data < 61) {
        var lastr = "temps de lecture : " + event.data + "second";
        document.getElementById("timer").innerHTML = lastr;
        }
        else {
            var lastr = "temps de lecture : " + event.data + "minute";
            document.getElementById("timer").innerHTML = lastr;
        }
        
    };
} 
else {
    alert("worker are not working")
    console.log("worker are desactivated")
}
